mapper.select:
SELECT 

FROM sc_orders a

WHERE a.id = #{id}

SELECT
FROM sc_orders a
AND a.resident_id = #{resident.id}
AND a.status = #{status}
AND r.name LIKE '%'||#{resident.name}||'%' '%'+#{resident.name}+'%' concat('%',#{resident.name},'%')
ANd a.belong_org_id = #{resident.belongOrg.id}
AND a.business_id = #{businessId}
AND a.goods_id = #{goods.id}
AND g.full_name LIKE '%'||#{goods.fullName}||'%' '%'+#{goods.fullName}+'%' concat('%',#{goods.fullName},'%')
AND a.code LIKE '%'||#{code}||'%' '%'+#{code}+'%' concat('%',#{code},'%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

mapper.update:
UPDATE sc_orders SET 	
	status = #{status}
WHERE id = #{id}

UPDATE sc_orders SET 	
	status = #{status},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE sc_orders SET 	
	com_status = #{comStatus},
	update_date = #{updateDate}
WHERE id = #{id}
